    public void registrarPersona(){
        int id = Integer.parseInt(txtId.getText());
        String nombre = txtNombre.getText();
        String familia = (String) cmbFamilia.getSelectedItem();
        String genero = (String) cmbGenero.getSelectedItem();
        int edad = Integer.parseInt(txtEdad.getText());
        String escolaridad =  (String) cmbEscolaridad.getSelectedItem();
        String trabajo = txtTrabajo.getText();
        
        Persona personas = new Persona(id, nombre, familia, edad, genero, escolaridad, trabajo);
        procesos.guardarPersona(personas);
   }
    
    public void volverInicio(){
         Inicio ventana = new Inicio(this,true);
        ventana.pack();
        setVisible(false);
        ventana.setVisible(true);
    }

